import React, { useState, useEffect } from "react";
import Web3 from "web3";
import "./App.css";

function App() {
  const [contract, setContract] = useState(null);
  const [person, setPerson] = useState(false);
  const [counter, setCounter] = useState(0);

  // Load the contract on component mount
  useEffect(() => {
    const init = async () => {
      // Connect to Ethereum network
      if (window.ethereum) {
        await window.ethereum.enable();
        const web3 = new Web3(window.ethereum);

        // Load the contract
        const contractAddress = web3.net;
        const contractABI = [
          {
            constant: false,
            inputs: [],
            name: "Vote",
            outputs: [],
            payable: false,
            stateMutability: "nonpayable",
            type: "function"
          },
          {
            constant: true,
            inputs: [],
            name: "counter",
            outputs: [{ internalType: "uint256", name: "", type: "uint256" }],
            payable: false,
            stateMutability: "view",
            type: "function"
          },
          {
            constant: true,
            inputs: [],
            name: "person",
            outputs: [{ internalType: "bool", name: "", type: "bool" }],
            payable: false,
            stateMutability: "view",
            type: "function"
          }
        ];
        const votingContract = new web3.eth.Contract(contractABI, contractAddress);

        setContract(votingContract);
      }
    };

    init();
  }, []);

  // Handle the voting button click
  const handleVoteClick = async () => {
    try {
      await contract.methods.Vote().send({ from: window.ethereum.selectedAddress });

      // Update the state
      const newCounter = await contract.methods.counter().call();
      setCounter(newCounter);

      if (newCounter === 10) {
        setPerson(true);
      }
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="App">
      <h1>Smart Contract Voting</h1>
      <p>Counter: {counter}</p>
      {person ? (
        <p>The person has voted 10 times.</p>
      ) : (
        <button onClick={handleVoteClick}>Vote</button>
      )}
    </div>
  );
}

export default App;
