import Voting from "../truffle_abis/voting.json"
import React, { useState, useEffect } from "react";
import Web3 from "web3";
import "./App.css";

function App() {
  const [contract, setContract] = useState(null);
  const [person, setPerson] = useState(false);
  const [counter, setCounter] = useState(0);

  // Load the contract on component mount
  useEffect(() => {
    const loadTheApp = async () => {
      // Connect to Ethereum network
      if (window.ethereum) {
        await window.ethereum.enable();
        const web3 = new Web3(window.ethereum);

        // Load the network
        const networkId = await web3.eth.net.getId();
        const contractAddress=  Voting.networks[networkId].address;// we get both the voting ABI frm the json file build at truffle_abis
        const contractABI = Voting.abi;
        const votingContract = new web3.eth.Contract(contractABI, contractAddress);

        setContract(votingContract);
      }
    };

    loadTheApp();
  }, []);

  // Handle the voting button click
  const handleVoteClick = async () => {
    try {
      await contract.methods.Vote().send({ from: window.ethereum.selectedAddress });

      // Update the state
      const newCounter = await contract.methods.counter().call();// it is providing me the string
      setCounter(parseInt(newCounter));

      if (newCounter === "8" || counter>=8) {//as newCounter is a string so we write it like so
        setPerson(true);
      }
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="App">
      <h1>Smart Contract Voting</h1>
      <p>Counter: {counter}</p>
      {person ? (
        <p>The person has voted 8 times.</p>
      ) : (
        <button onClick={handleVoteClick}>Vote</button>
      )}
      <h2> person status</h2>
      
    </div>
  );
}

export default App;
